css:
@media print {
    body {
        margin: 0;
        padding: 0;
    }
}

.print-container {
    width: 100%;
    padding: 30px;
    font-family: Arial, sans-serif;
    font-size: 18px;
}

.header {
    text-align: center;
    margin-bottom: 30px;
}

.header img {
    height: 100px;
    margin-bottom: 15px;
}

.header h2 {
    font-weight: bold;
    margin: 5px 0;
    font-size: 28px;
}

.header p {
    margin: 0;
    font-size: 18px;
}

.label-bold {
    font-weight: bold;
    width: 30%;
    font-size: 18px;
}

.value {
    width: 70%;
    font-size: 18px;
}

.row {
    display: flex;
    margin-bottom: 12px;
}

.status-active {
    color: green;
    font-weight: bold;
    font-size: 18px;
}

.status-expired {
    color: red;
    font-weight: bold;
    font-size: 18px;
}

.status-other {
    color: orange;
    font-weight: bold;
    font-size: 18px;
}
