css:
.print-container {
    width: 100%;
    max-width: 800px;
    margin: auto;
    padding: 40px;
    font-family: 'Arial', sans-serif;
    font-size: 16px;
    border: 1px solid #ccc;
}

.header {
    text-align: center;
    margin-bottom: 30px;
}

.header img {
    max-height: 80px;
}

.header h2 {
    font-size: 32px;
    margin: 10px 0;
    font-weight: bold;
}

.header h3 {
    margin-top: 20px;
    font-size: 24px;
    text-decoration: underline;
}

.row {
    display: flex;
    margin-bottom: 10px;
}

.label-bold {
    width: 200px;
    font-weight: bold;
}

.value {
    flex: 1;
}

.status-paid {
    color: green;
    font-weight: bold;
}

.status-unpaid {
    color: red;
    font-weight: bold;
}

.status-pending {
    color: orange;
    font-weight: bold;
}
